import{_ as o,p as e,q as c,Z as s}from"./framework-87c4c4c1.js";const t={},l=s('<h1 id="说一说cookie、sessionstorage、localstorage区别" tabindex="-1"><a class="header-anchor" href="#说一说cookie、sessionstorage、localstorage区别" aria-hidden="true">#</a> 说一说cookie、sessionStorage、localStorage区别？</h1><hr><p><strong>共同点：</strong> 都存储在浏览器本地</p><p><strong>存储的时间有效期不同</strong></p><ol><li><code>cookie</code>的有效期可以设置，默认是关闭浏览器后失效。</li><li><code>sessionStorage</code>的有效期是仅保持在当前页面，关闭当前页面或者关闭浏览器就会失效。</li><li><code>localStorage</code>的有效期是在不进行手动删除的情况下是一直有效的。</li></ol><p><strong>存储的大小不同</strong></p><ol><li><code>cookie</code>的存储时4kb左右</li><li><code>localStorage</code>和<code>sessionStorage</code>的存储容量是5MB</li></ol><p><strong>作用域不用</strong></p><ol><li><code>sessionStorage</code>不在不同的浏览器窗口中共享</li><li><code>localStorage</code>和<code>cookie</code>在所有同源窗口中都是共享的</li></ol>',9),r=[l];function a(i,d){return e(),c("div",null,r)}const g=o(t,[["render",a],["__file","storage.html.vue"]]);export{g as default};
